import React, { useState } from 'react';
import './QuickAddWidget.css';

const QuickAddWidget = ({ onAddExpense }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [amount, setAmount] = useState('');
    const [description, setDescription] = useState('');
    const [category, setCategory] = useState('');

    const quickCategories = [
        { name: 'Food', icon: 'ðŸ•', color: '#FF6B6B' },
        { name: 'Transport', icon: 'ðŸš—', color: '#4ECDC4' },
        { name: 'Shopping', icon: 'ðŸ›ï¸', color: '#45B7D1' },
        { name: 'Bills', icon: 'ðŸ“„', color: '#96CEB4' },
        { name: 'Entertainment', icon: 'ðŸŽ¬', color: '#FFEAA7' },
        { name: 'Health', icon: 'ðŸ’Š', color: '#DDA0DD' },
        { name: 'Education', icon: 'ðŸ“š', color: '#98D8C8' },
        { name: 'Other', icon: 'ðŸ“Œ', color: '#F7DC6F' }
    ];

    const handleSubmit = (e) => {
        e.preventDefault();
        if (amount && description && category) {
            onAddExpense({
                amount: parseFloat(amount),
                description,
                category,
                date: new Date().toISOString()
            });
            setAmount('');
            setDescription('');
            setCategory('');
            setIsOpen(false);
        }
    };

    const handleQuickAdd = (cat) => {
        setCategory(cat.name);
        if (amount && description) {
            handleSubmit({ preventDefault: () => { } });
        }
    };

    return (
        <>
            {/* Floating Action Button */}
            <div className="quick-add-fab" onClick={() => setIsOpen(true)}>
                <span className="fab-icon">+</span>
                <span className="fab-label">Quick Add</span>
            </div>

            {/* Quick Add Modal */}
            {isOpen && (
                <div className="quick-add-overlay" onClick={() => setIsOpen(false)}>
                    <div className="quick-add-modal" onClick={(e) => e.stopPropagation()}>
                        <div className="quick-add-header">
                            <h3>âš¡ Quick Add Expense</h3>
                            <button className="close-btn" onClick={() => setIsOpen(false)}>Ã—</button>
                        </div>

                        <form onSubmit={handleSubmit} className="quick-add-form">
                            <div className="amount-input-group">
                                <span className="currency-prefix">RPS</span>
                                <input
                                    type="number"
                                    value={amount}
                                    onChange={(e) => setAmount(e.target.value)}
                                    placeholder="0.00"
                                    min="0"
                                    step="0.01"
                                    required
                                    autoFocus
                                />
                            </div>

                            <input
                                type="text"
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                placeholder="What was it for?"
                                required
                                className="description-input"
                            />

                            <div className="category-grid">
                                {quickCategories.map((cat) => (
                                    <button
                                        key={cat.name}
                                        type="button"
                                        className={`category-btn ${category === cat.name ? 'selected' : ''}`}
                                        onClick={() => setCategory(cat.name)}
                                        style={{ '--category-color': cat.color }}
                                    >
                                        <span className="category-icon">{cat.icon}</span>
                                        <span className="category-name">{cat.name}</span>
                                    </button>
                                ))}
                            </div>

                            <div className="quick-actions">
                                <button
                                    type="submit"
                                    className="add-expense-btn"
                                    disabled={!amount || !description || !category}
                                >
                                    Add Expense
                                </button>
                            </div>
                        </form>

                        {/* Quick Category Buttons */}
                        {amount && description && (
                            <div className="quick-category-suggestions">
                                <p>Quick add with category:</p>
                                <div className="quick-category-buttons">
                                    {quickCategories.slice(0, 4).map((cat) => (
                                        <button
                                            key={cat.name}
                                            className="quick-cat-btn"
                                            onClick={() => handleQuickAdd(cat)}
                                            style={{ '--category-color': cat.color }}
                                        >
                                            {cat.icon} {cat.name}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </>
    );
};

export default QuickAddWidget;
