import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiHeart, FiTrendingUp, FiTrendingDown, FiSmile, FiActivity, FiTarget, FiBookOpen, FiZap, FiPlay, FiBarChart3, FiDollarSign, FiAlertCircle } from 'react-icons/fi';
import WellnessGame from '../components/WellnessGame';

const Wellness = () => {
    const [expenses, setExpenses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedPeriod, setSelectedPeriod] = useState('month');
    const [showWellnessGame, setShowWellnessGame] = useState(false);

    useEffect(() => {
        fetchExpenses();
    }, [selectedPeriod]);

    const fetchExpenses = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`/api/expenses?period=${selectedPeriod}`);
            // Extract expenses array from response
            const expensesData = response.data.expenses || response.data || [];
            setExpenses(expensesData);
        } catch (error) {
            setError('Failed to fetch expenses');
            console.error('Error fetching expenses:', error);
        } finally {
            setLoading(false);
        }
    };

    // Calculate mood-spending insights
    const calculateMoodInsights = () => {
        const moodData = {};
        let totalSpent = 0;
        let emotionalSpendingCount = 0;
        let emotionalSpendingTotal = 0;

        // Ensure expenses is an array
        if (!Array.isArray(expenses)) {
            return {
                moodData: {},
                totalSpent: 0,
                emotionalSpendingCount: 0,
                emotionalSpendingTotal: 0,
                emotionalSpendingPercentage: 0
            };
        }

        expenses.forEach(expense => {
            const mood = expense.mood || 'neutral';
            if (!moodData[mood]) {
                moodData[mood] = { count: 0, total: 0, avgAmount: 0 };
            }
            moodData[mood].count++;
            moodData[mood].total += expense.amount;
            totalSpent += expense.amount;

            if (expense.emotionalSpending) {
                emotionalSpendingCount++;
                emotionalSpendingTotal += expense.amount;
            }
        });

        // Calculate averages
        Object.keys(moodData).forEach(mood => {
            moodData[mood].avgAmount = moodData[mood].total / moodData[mood].count;
        });

        return {
            moodData,
            totalSpent,
            emotionalSpendingCount,
            emotionalSpendingTotal,
            emotionalSpendingPercentage: totalSpent > 0 ? (emotionalSpendingTotal / totalSpent) * 100 : 0
        };
    };

    // Get wellness tips based on spending patterns
    const getWellnessTips = () => {
        const insights = calculateMoodInsights();
        const tips = [];

        // Tip based on emotional spending
        if (insights.emotionalSpendingPercentage > 30) {
            tips.push({
                icon: 'üí≠',
                title: 'Emotional Spending Alert',
                content: 'You\'re spending a significant amount when emotional. Try waiting 24 hours before making non-essential purchases.',
                type: 'warning',
                color: 'from-red-500 to-pink-500'
            });
        }

        // Tip based on mood patterns
        const moodData = insights.moodData;
        if (moodData.sad && moodData.sad.avgAmount > 50) {
            tips.push({
                icon: 'üò¢',
                title: 'Sadness & Spending',
                content: 'You tend to spend more when feeling sad. Consider alternative activities like calling a friend or going for a walk.',
                type: 'insight',
                color: 'from-blue-500 to-indigo-500'
            });
        }

        if (moodData.stressed && moodData.stressed.avgAmount > 40) {
            tips.push({
                icon: 'üò∞',
                title: 'Stress Spending Pattern',
                content: 'Stress seems to trigger higher spending. Try deep breathing exercises before making purchases.',
                type: 'insight',
                color: 'from-orange-500 to-red-500'
            });
        }

        // General wellness tips
        tips.push({
            icon: 'üéØ',
            title: 'Mindful Spending',
            content: 'Before each purchase, ask yourself: "Do I need this, or do I want this?" Take a moment to reflect.',
            type: 'tip',
            color: 'from-green-500 to-emerald-500'
        });

        tips.push({
            icon: 'üì±',
            title: 'Digital Detox',
            content: 'Unsubscribe from shopping emails and remove shopping apps from your phone to reduce impulse buying.',
            type: 'tip',
            color: 'from-purple-500 to-pink-500'
        });

        return tips;
    };

    const insights = calculateMoodInsights();
    const wellnessTips = getWellnessTips();

    const moodEmojis = {
        happy: 'üòä',
        sad: 'üò¢',
        stressed: 'üò∞',
        excited: 'ü§©',
        neutral: 'üòê',
        anxious: 'üò®',
        content: 'üòå',
        frustrated: 'üò§'
    };

    const moodColors = {
        happy: 'bg-gradient-to-r from-green-400 to-emerald-500',
        sad: 'bg-gradient-to-r from-blue-400 to-indigo-500',
        stressed: 'bg-gradient-to-r from-red-400 to-pink-500',
        excited: 'bg-gradient-to-r from-yellow-400 to-orange-500',
        neutral: 'bg-gradient-to-r from-gray-400 to-slate-500',
        anxious: 'bg-gradient-to-r from-purple-400 to-violet-500',
        content: 'bg-gradient-to-r from-teal-400 to-cyan-500',
        frustrated: 'bg-gradient-to-r from-orange-400 to-red-500'
    };

    if (loading) {
        return (
            <div className="container">
                <div className="page-header">
                    <h1 className="page-title">Financial Wellness</h1>
                    <p className="page-subtitle">Understanding your spending patterns and mental health</p>
                </div>
                <div className="text-center py-16">
                    <div className="animate-spin rounded-full h-16 w-16 border-4 border-blue-600 border-t-transparent mx-auto mb-4"></div>
                    <p className="text-lg text-gray-600 font-medium">Loading your wellness insights...</p>
                    <p className="text-sm text-gray-500 mt-2">Analyzing your spending patterns</p>
                </div>
            </div>
        );
    }

    return (
        <div className="container">
            {/* Hero Section */}
            <div className="bg-gradient-to-br from-blue-600 via-purple-600 to-pink-600 rounded-2xl p-8 mb-8 text-white shadow-xl">
                <div className="flex items-center justify-between">
                    <div>
                        <h1 className="text-4xl font-bold mb-2 flex items-center">
                            <FiHeart className="mr-4 text-red-300 animate-pulse" />
                            Financial Wellness
                        </h1>
                        <p className="text-xl opacity-90 mb-4">Understanding your spending patterns and mental health</p>
                        <div className="flex items-center space-x-4">
                            <div className="bg-white bg-opacity-20 rounded-lg px-4 py-2">
                                <span className="text-sm opacity-90">Total Expenses</span>
                                <p className="text-2xl font-bold">${insights.totalSpent.toFixed(2)}</p>
                            </div>
                            <div className="bg-white bg-opacity-20 rounded-lg px-4 py-2">
                                <span className="text-sm opacity-90">Emotional Spending</span>
                                <p className="text-2xl font-bold">{insights.emotionalSpendingPercentage.toFixed(1)}%</p>
                            </div>
                        </div>
                    </div>
                    <div className="text-right">
                        <div className="text-6xl mb-2">üß†</div>
                        <p className="text-sm opacity-80">Mindful Spending</p>
                    </div>
                </div>
            </div>

            {error && (
                <div className="alert alert-error mb-6 bg-red-50 border-red-200 text-red-800">
                    <FiAlertCircle className="mr-2" />
                    {error}
                </div>
            )}

            {/* Period Selector */}
            <div className="mb-8">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                    <FiBarChart3 className="mr-2" />
                    Select Time Period
                </h3>
                <div className="flex gap-3">
                    {['week', 'month', 'quarter', 'year'].map(period => (
                        <button
                            key={period}
                            onClick={() => setSelectedPeriod(period)}
                            className={`px-6 py-3 rounded-xl text-sm font-medium transition-all duration-300 transform hover:scale-105 ${selectedPeriod === period
                                    ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                                    : 'bg-white text-gray-700 hover:bg-gray-50 border border-gray-200 shadow-sm'
                                }`}
                        >
                            {period.charAt(0).toUpperCase() + period.slice(1)}
                        </button>
                    ))}
                </div>
            </div>

            {/* Key Metrics Cards */}
            <div className="grid md:grid-cols-3 gap-6 mb-8">
                <div className="bg-gradient-to-br from-green-400 to-emerald-500 p-6 rounded-2xl text-white shadow-lg transform hover:scale-105 transition-transform duration-300">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-sm opacity-90 mb-1">Total Spent</p>
                            <p className="text-3xl font-bold">${insights.totalSpent.toFixed(2)}</p>
                        </div>
                        <div className="text-4xl">üí∞</div>
                    </div>
                </div>

                <div className="bg-gradient-to-br from-purple-400 to-pink-500 p-6 rounded-2xl text-white shadow-lg transform hover:scale-105 transition-transform duration-300">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-sm opacity-90 mb-1">Emotional Purchases</p>
                            <p className="text-3xl font-bold">{insights.emotionalSpendingCount}</p>
                        </div>
                        <div className="text-4xl">üí≠</div>
                    </div>
                </div>

                <div className="bg-gradient-to-br from-orange-400 to-red-500 p-6 rounded-2xl text-white shadow-lg transform hover:scale-105 transition-transform duration-300">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-sm opacity-90 mb-1">Emotional Spending %</p>
                            <p className="text-3xl font-bold">{insights.emotionalSpendingPercentage.toFixed(1)}%</p>
                        </div>
                        <div className="text-4xl">üìä</div>
                    </div>
                </div>
            </div>

            {/* Mood-Spending Analysis & Wellness Tips */}
            <div className="grid lg:grid-cols-2 gap-8 mb-8">
                <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
                    <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                        <FiActivity className="mr-3 text-blue-600" />
                        Mood-Spending Patterns
                    </h3>
                    <div className="space-y-4">
                        {Object.entries(insights.moodData).map(([mood, data]) => (
                            <div key={mood} className={`p-4 rounded-xl text-white ${moodColors[mood]} shadow-md transform hover:scale-102 transition-transform duration-200`}>
                                <div className="flex items-center justify-between">
                                    <div className="flex items-center">
                                        <span className="text-3xl mr-4">{moodEmojis[mood]}</span>
                                        <div>
                                            <p className="font-semibold text-lg capitalize">{mood}</p>
                                            <p className="text-sm opacity-90">{data.count} purchases</p>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <p className="font-bold text-xl">${data.total.toFixed(2)}</p>
                                        <p className="text-sm opacity-90">Avg: ${data.avgAmount.toFixed(2)}</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Wellness Tips */}
                <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
                    <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                        <FiBookOpen className="mr-3 text-green-600" />
                        Personalized Wellness Tips
                    </h3>
                    <div className="space-y-4">
                        {wellnessTips.map((tip, index) => (
                            <div key={index} className={`p-5 rounded-xl text-white shadow-md transform hover:scale-102 transition-all duration-300 bg-gradient-to-r ${tip.color}`}>
                                <div className="flex items-start">
                                    <span className="text-3xl mr-4">{tip.icon}</span>
                                    <div>
                                        <h4 className="font-semibold text-lg mb-2">{tip.title}</h4>
                                        <p className="text-sm opacity-90 leading-relaxed">{tip.content}</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Wellness Game Section */}
            <div className="bg-gradient-to-br from-purple-600 via-pink-600 to-orange-500 p-8 rounded-2xl text-white shadow-xl mb-8 transform hover:scale-102 transition-transform duration-300">
                <div className="flex items-center justify-between">
                    <div>
                        <h3 className="text-2xl font-bold mb-3 flex items-center">
                            <FiPlay className="mr-3" />
                            Interactive Wellness Games
                        </h3>
                        <p className="text-lg opacity-90 mb-4">
                            Practice mindful spending with interactive exercises and build healthier financial habits.
                        </p>
                        <div className="flex items-center space-x-4 text-sm opacity-80">
                            <span>üßò‚Äç‚ôÄÔ∏è Breathing Exercises</span>
                            <span>üí≠ Reflection Practice</span>
                            <span>üôè Gratitude Journal</span>
                        </div>
                    </div>
                    <button
                        onClick={() => setShowWellnessGame(true)}
                        className="bg-white text-purple-600 px-8 py-4 rounded-xl font-bold text-lg hover:bg-gray-100 transition-all duration-300 transform hover:scale-105 shadow-lg"
                    >
                        Play Games
                    </button>
                </div>
            </div>

            {/* Quick Wellness Actions */}
            <div className="bg-gradient-to-br from-blue-600 via-indigo-600 to-purple-600 p-8 rounded-2xl text-white shadow-xl">
                <h3 className="text-2xl font-bold mb-6 flex items-center">
                    <FiZap className="mr-3" />
                    Quick Wellness Actions
                </h3>
                <div className="grid md:grid-cols-3 gap-6">
                    <div className="text-center bg-white bg-opacity-10 rounded-xl p-6 backdrop-blur-sm">
                        <div className="text-5xl mb-4">üßò‚Äç‚ôÄÔ∏è</div>
                        <h4 className="font-semibold text-lg mb-2">Mindful Breathing</h4>
                        <p className="text-sm opacity-90">Take 3 deep breaths before making a purchase</p>
                    </div>
                    <div className="text-center bg-white bg-opacity-10 rounded-xl p-6 backdrop-blur-sm">
                        <div className="text-5xl mb-4">üìù</div>
                        <h4 className="font-semibold text-lg mb-2">Emotional Check-in</h4>
                        <p className="text-sm opacity-90">Write down your feelings before spending</p>
                    </div>
                    <div className="text-center bg-white bg-opacity-10 rounded-xl p-6 backdrop-blur-sm">
                        <div className="text-5xl mb-4">‚è∞</div>
                        <h4 className="font-semibold text-lg mb-2">24-Hour Rule</h4>
                        <p className="text-sm opacity-90">Wait 24 hours for non-essential purchases</p>
                    </div>
                </div>
            </div>

            {/* Wellness Game Modal */}
            <WellnessGame
                isOpen={showWellnessGame}
                onClose={() => setShowWellnessGame(false)}
            />
        </div>
    );
};

export default Wellness;
