import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { FiUser, FiMail, FiDollarSign, FiSave, FiShield, FiCalendar, FiTrendingUp } from 'react-icons/fi';

const Profile = () => {
    const { user, updateProfile } = useAuth();
    const [formData, setFormData] = useState({
        name: '',
        currency: 'INR',
        monthlyBudget: ''
    });
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState('');
    const [error, setError] = useState('');

    const currencies = [
        { code: 'INR', symbol: '₹', name: 'Indian Rupee' },
        { code: 'USD', symbol: '$', name: 'US Dollar' },
        { code: 'EUR', symbol: '€', name: 'Euro' },
        { code: 'GBP', symbol: '£', name: 'British Pound' },
        { code: 'JPY', symbol: '¥', name: 'Japanese Yen' },
        { code: 'CAD', symbol: 'C$', name: 'Canadian Dollar' },
        { code: 'AUD', symbol: 'A$', name: 'Australian Dollar' },
        { code: 'CHF', symbol: 'CHF', name: 'Swiss Franc' },
        { code: 'CNY', symbol: '¥', name: 'Chinese Yuan' }
    ];

    useEffect(() => {
        if (user) {
            setFormData({
                name: user.name || '',
                currency: user.currency || 'INR',
                monthlyBudget: user.monthlyBudget ? user.monthlyBudget.toString() : ''
            });
        }
    }, [user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccess('');
        setError('');

        try {
            const result = await updateProfile({
                name: formData.name,
                currency: formData.currency,
                monthlyBudget: formData.monthlyBudget ? parseFloat(formData.monthlyBudget) : 0
            });

            if (result.success) {
                setSuccess('Profile updated successfully!');
            } else {
                setError(result.message);
            }
        } catch (error) {
            setError('Failed to update profile');
        } finally {
            setLoading(false);
        }
    };

    const getCurrencySymbol = (code) => {
        const currency = currencies.find(c => c.code === code);
        return currency ? currency.symbol : '₹';
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'N/A';
        return new Date(dateString).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    return (
        <div className="container">
            <div className="section-header">
                <h1 className="text-white">Profile Settings</h1>
                <div className="section-subtitle">Manage your account preferences and settings</div>
            </div>

            <div className="card mb-6">
                <h3 className="text-primary mb-6 text-xl">Account Information</h3>

                {success && (
                    <div className="alert alert-success mb-4">
                        {success}
                    </div>
                )}

                {error && (
                    <div className="alert alert-error mb-4">
                        {error}
                    </div>
                )}

                <form onSubmit={handleSubmit}>
                    <div className="grid md:grid-cols-2 gap-6">
                        <div className="form-group">
                            <label htmlFor="name" className="form-label">
                                <FiUser className="inline mr-2" />
                                Full Name
                            </label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                className="form-input"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="email" className="form-label">
                                <FiMail className="inline mr-2" />
                                Email Address
                            </label>
                            <input
                                type="email"
                                id="email"
                                value={user?.email || ''}
                                className="form-input"
                                disabled
                            />
                            <small className="text-muted">Email cannot be changed</small>
                        </div>

                        <div className="form-group">
                            <label htmlFor="currency" className="form-label">
                                <FiDollarSign className="inline mr-2" />
                                Currency
                            </label>
                            <select
                                id="currency"
                                name="currency"
                                value={formData.currency}
                                onChange={handleChange}
                                className="form-select"
                            >
                                {currencies.map(currency => (
                                    <option key={currency.code} value={currency.code}>
                                        {currency.symbol} {currency.name} ({currency.code})
                                    </option>
                                ))}
                            </select>
                        </div>

                        <div className="form-group">
                            <label htmlFor="monthlyBudget" className="form-label">
                                <FiTrendingUp className="inline mr-2" />
                                Monthly Budget
                            </label>
                            <div className="relative">
                                <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 font-medium">
                                    {getCurrencySymbol(formData.currency)}
                                </span>
                                <input
                                    type="number"
                                    id="monthlyBudget"
                                    name="monthlyBudget"
                                    value={formData.monthlyBudget}
                                    onChange={handleChange}
                                    className="form-input pl-8"
                                    step="0.01"
                                    min="0"
                                    placeholder="0.00"
                                />
                            </div>
                            <small className="text-muted">
                                Set a monthly budget to track your spending goals
                            </small>
                        </div>
                    </div>

                    <div className="btn-group mt-8">
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading}
                        >
                            <FiSave />
                            {loading ? 'Saving...' : 'Save Changes'}
                        </button>
                    </div>
                </form>
            </div>

            <div className="card">
                <h3 className="text-primary mb-6 text-xl">Account Overview</h3>
                <div className="space-y-4">
                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center">
                            <FiShield className="text-green-500 mr-3" />
                            <span className="text-secondary">Account Status</span>
                        </div>
                        <span className="badge badge-success">Active</span>
                    </div>

                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center">
                            <FiCalendar className="text-blue-500 mr-3" />
                            <span className="text-secondary">Member Since</span>
                        </div>
                        <span className="text-primary font-medium">
                            {formatDate(user?.createdAt)}
                        </span>
                    </div>

                    <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center">
                            <FiDollarSign className="text-purple-500 mr-3" />
                            <span className="text-secondary">Currency</span>
                        </div>
                        <span className="text-primary font-medium">
                            {formData.currency}
                        </span>
                    </div>

                    {user?.monthlyBudget && (
                        <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                            <div className="flex items-center">
                                <FiTrendingUp className="text-orange-500 mr-3" />
                                <span className="text-secondary">Monthly Budget</span>
                            </div>
                            <span className="text-primary font-medium">
                                {getCurrencySymbol(formData.currency)}{user.monthlyBudget}
                            </span>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Profile;
