import React, { useState, useEffect } from 'react';
import './CurrencyConverter.css';

const CurrencyConverter = ({ isOpen, onClose }) => {
    const [fromCurrency, setFromCurrency] = useState('INR');
    const [toCurrency, setToCurrency] = useState('USD');
    const [fromAmount, setFromAmount] = useState('');
    const [toAmount, setToAmount] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const currencies = [
        { code: 'INR', name: 'Indian Rupee', symbol: 'RPS' },
        { code: 'USD', name: 'US Dollar', symbol: '$' },
        { code: 'EUR', name: 'Euro', symbol: '€' },
        { code: 'GBP', name: 'British Pound', symbol: '£' },
        { code: 'JPY', name: 'Japanese Yen', symbol: '¥' },
        { code: 'CAD', name: 'Canadian Dollar', symbol: 'C$' },
        { code: 'AUD', name: 'Australian Dollar', symbol: 'A$' },
        { code: 'CHF', name: 'Swiss Franc', symbol: 'CHF' }
    ];

    const convertCurrency = async () => {
        if (!fromAmount || isNaN(fromAmount) || parseFloat(fromAmount) <= 0) {
            setError('Please enter a valid amount');
            return;
        }
        if (!fromCurrency || !toCurrency) {
            setError('Please select both currencies');
            return;
        }

        setLoading(true);
        setError('');
        setToAmount('');

        try {
            const response = await fetch(`https://api.exchangerate.host/convert?from=${fromCurrency}&to=${toCurrency}&amount=${fromAmount}`);
            const data = await response.json();
            // Debug: log the API response
            console.log('Currency API response:', data);
            if (data && (typeof data.result === 'number' || !isNaN(Number(data.result)))) {
                setToAmount(Number(data.result).toFixed(2));
            } else if (data && data.error) {
                // Log the full error object
                console.error('Currency API error:', data.error);
                // Try to show a helpful message
                if (typeof data.error === 'string') {
                    setError('API error: ' + data.error);
                } else if (data.error.message) {
                    setError('API error: ' + data.error.message);
                } else if (data.error.code) {
                    setError('API error: ' + data.error.code);
                } else {
                    setError('API error: Unexpected error from API.');
                }
            } else {
                setError('Conversion failed. Please try again later.');
            }
        } catch (err) {
            setError('Network error. Please check your connection.');
        } finally {
            setLoading(false);
        }
    };

    const handleSwap = () => {
        setFromCurrency(toCurrency);
        setToCurrency(fromCurrency);
        setFromAmount(toAmount);
        setToAmount('');
    };

    const getCurrencySymbol = (code) => {
        const currency = currencies.find(c => c.code === code);
        return currency ? currency.symbol : code;
    };

    const handleFromAmountChange = (value) => {
        setFromAmount(value);
        setToAmount(''); // Clear converted amount when input changes
    };

    if (!isOpen) return null;

    return (
        <div className="currency-converter-overlay" onClick={onClose}>
            <div className="currency-converter-modal" onClick={(e) => e.stopPropagation()}>
                <div className="currency-converter-header">
                    <h3>💱 Currency Converter</h3>
                    <button className="close-btn" onClick={onClose}>×</button>
                </div>

                <div className="currency-converter-content">
                    <div className="conversion-inputs">
                        {/* From Currency Section */}
                        <div className="conversion-section">
                            <label className="section-label">From</label>
                            <div className="currency-input-group">
                                <div className="currency-selector">
                                    <select
                                        value={fromCurrency}
                                        onChange={(e) => setFromCurrency(e.target.value)}
                                    >
                                        {currencies.map(currency => (
                                            <option key={currency.code} value={currency.code}>
                                                {currency.code} - {currency.name}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                                <div className="amount-input-container">
                                    <span className="currency-symbol">{getCurrencySymbol(fromCurrency)}</span>
                                    <input
                                        type="number"
                                        value={fromAmount}
                                        onChange={(e) => handleFromAmountChange(e.target.value)}
                                        placeholder="Enter amount (e.g. 1000)"
                                        min="0"
                                        step="0.01"
                                        className="amount-input no-overlap"
                                    />
                                </div>
                            </div>
                        </div>

                        {/* Swap Button */}
                        <button className="swap-btn" onClick={handleSwap}>
                            ⇄ Swap
                        </button>

                        {/* To Currency Section */}
                        <div className="conversion-section">
                            <label className="section-label">To</label>
                            <div className="currency-input-group">
                                <div className="currency-selector">
                                    <select
                                        value={toCurrency}
                                        onChange={(e) => setToCurrency(e.target.value)}
                                    >
                                        {currencies.map(currency => (
                                            <option key={currency.code} value={currency.code}>
                                                {currency.code} - {currency.name}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                                <div className="amount-input-container">
                                    <span className="currency-symbol">{getCurrencySymbol(toCurrency)}</span>
                                    <input
                                        type="text"
                                        value={toAmount}
                                        readOnly
                                        placeholder="Converted amount"
                                        className="amount-input result-input no-overlap"
                                    />
                                </div>
                            </div>
                        </div>
                    </div>

                    {error && <div className="error-message">{error}</div>}

                    <button
                        className="convert-btn"
                        onClick={convertCurrency}
                        disabled={loading || !fromAmount}
                    >
                        {loading ? 'Converting...' : 'Convert'}
                    </button>

                    <div className="converter-info">
                        <p>💡 Tip: Use this for international transactions, online shopping, or travel planning!</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CurrencyConverter;
