import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
    FiHome,
    FiDollarSign,
    FiPieChart,
    FiRepeat,
    FiUser,
    FiLogOut,
    FiMenu,
    FiX,
    FiGlobe,
    FiUsers,
    FiMoon,
    FiSun
} from 'react-icons/fi';

const Navbar = ({ onShowCurrencyConverter, onShowExpenseSplitter }) => {
    const { user, logout } = useAuth();
    const location = useLocation();
    const navigate = useNavigate();
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isDarkMode, setIsDarkMode] = useState(() => {
        const saved = localStorage.getItem('darkMode');
        return saved ? JSON.parse(saved) : true;
    });

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    const toggleDarkMode = () => {
        setIsDarkMode(!isDarkMode);
        localStorage.setItem('darkMode', JSON.stringify(!isDarkMode));
        document.documentElement.setAttribute('data-theme', !isDarkMode ? 'dark' : 'light');
    };

    const navItems = [
        { path: '/', label: 'Dashboard', icon: FiHome },
        { path: '/expenses', label: 'Expenses', icon: FiDollarSign },
        { path: '/categories', label: 'Categories', icon: FiPieChart },
        { path: '/recurring', label: 'Recurring', icon: FiRepeat },
        { path: '/profile', label: 'Profile', icon: FiUser }
    ];

    const isActive = (path) => {
        if (path === '/') {
            return location.pathname === '/';
        }
        return location.pathname.startsWith(path);
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-brand">
                    Expense Tracker
                </Link>

                {/* Desktop Navigation */}
                <div className="navbar-nav desktop-nav">
                    {navItems.map((item) => {
                        const Icon = item.icon;
                        return (
                            <Link
                                key={item.path}
                                to={item.path}
                                className={`nav-link ${isActive(item.path) ? 'active' : ''}`}
                            >
                                <span className="nav-link-icon">
                                    <Icon />
                                    <span className="nav-link-text">{item.label}</span>
                                </span>
                            </Link>
                        );
                    })}
                </div>

                {/* Feature Buttons */}
                <div className="feature-buttons">
                    <button
                        className="feature-btn"
                        onClick={onShowCurrencyConverter}
                        title="Currency Converter"
                    >
                        <FiGlobe />
                        <span className="feature-btn-text">Convert</span>
                    </button>

                    <button
                        className="feature-btn"
                        onClick={onShowExpenseSplitter}
                        title="Split Expense"
                    >
                        <FiUsers />
                        <span className="feature-btn-text">Split</span>
                    </button>

                    <button
                        className="feature-btn"
                        onClick={toggleDarkMode}
                        title={`Switch to ${isDarkMode ? 'light' : 'dark'} mode`}
                    >
                        {isDarkMode ? <FiSun /> : <FiMoon />}
                        <span className="feature-btn-text">{isDarkMode ? 'Light' : 'Dark'}</span>
                    </button>
                </div>

                {/* User Menu */}
                <div className="user-menu">
                    <span className="user-name">{user?.name}</span>
                    <button onClick={handleLogout} className="btn btn-outline btn-sm">
                        <FiLogOut />
                        <span className="nav-link-text">Logout</span>
                    </button>
                </div>

                {/* Mobile Menu Button */}
                <button
                    className="mobile-menu-btn"
                    onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                >
                    {isMobileMenuOpen ? <FiX /> : <FiMenu />}
                </button>
            </div>

            {/* Mobile Navigation */}
            {isMobileMenuOpen && (
                <div className="mobile-nav">
                    {navItems.map((item) => {
                        const Icon = item.icon;
                        return (
                            <Link
                                key={item.path}
                                to={item.path}
                                className={`nav-link ${isActive(item.path) ? 'active' : ''}`}
                                onClick={() => setIsMobileMenuOpen(false)}
                            >
                                <span className="nav-link-icon">
                                    <Icon />
                                    <span className="nav-link-text">{item.label}</span>
                                </span>
                            </Link>
                        );
                    })}

                    {/* Mobile Feature Buttons */}
                    <div className="mobile-feature-buttons">
                        <button
                            className="feature-btn"
                            onClick={() => {
                                onShowCurrencyConverter();
                                setIsMobileMenuOpen(false);
                            }}
                        >
                            <FiGlobe />
                            <span className="nav-link-text">Currency Converter</span>
                        </button>

                        <button
                            className="feature-btn"
                            onClick={() => {
                                onShowExpenseSplitter();
                                setIsMobileMenuOpen(false);
                            }}
                        >
                            <FiUsers />
                            <span className="nav-link-text">Split Expense</span>
                        </button>

                        <button
                            className="feature-btn"
                            onClick={() => {
                                toggleDarkMode();
                                setIsMobileMenuOpen(false);
                            }}
                        >
                            {isDarkMode ? <FiSun /> : <FiMoon />}
                            <span className="nav-link-text">{isDarkMode ? 'Light Mode' : 'Dark Mode'}</span>
                        </button>
                    </div>

                    <div className="mobile-user-menu">
                        <span className="user-name">{user?.name}</span>
                        <button onClick={handleLogout} className="btn btn-outline btn-sm">
                            <FiLogOut />
                            <span className="nav-link-text">Logout</span>
                        </button>
                    </div>
                </div>
            )}
        </nav>
    );
};

export default Navbar;
