import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { format } from 'date-fns';
import {
    FiPlus,
    FiSearch,
    FiFilter,
    FiDownload,
    FiEdit2,
    FiTrash2,
    FiCalendar,
    FiMapPin,
    FiDollarSign
} from 'react-icons/fi';

const Expenses = () => {
    const [expenses, setExpenses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [filters, setFilters] = useState({
        category: '',
        startDate: '',
        endDate: '',
        search: ''
    });
    const [showFilters, setShowFilters] = useState(false);

    const categories = [
        'Food', 'Transport', 'Entertainment', 'Shopping',
        'Bills', 'Healthcare', 'Education', 'Travel', 'Other'
    ];

    useEffect(() => {
        fetchExpenses();
    }, [currentPage, filters]);

    const fetchExpenses = async () => {
        try {
            setLoading(true);
            const params = {
                page: currentPage,
                limit: 10,
                ...filters
            };

            const response = await axios.get('/api/expenses', { params });
            setExpenses(response.data.expenses);
            setTotalPages(response.data.totalPages);
        } catch (error) {
            console.error('Error fetching expenses:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleFilterChange = (e) => {
        setFilters({
            ...filters,
            [e.target.name]: e.target.value
        });
        setCurrentPage(1);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this expense?')) {
            try {
                await axios.delete(`/api/expenses/${id}`);
                fetchExpenses();
            } catch (error) {
                console.error('Error deleting expense:', error);
            }
        }
    };

    const handleExport = async () => {
        try {
            const params = new URLSearchParams(filters);
            const response = await axios.get(`/api/expenses/export?${params}`, {
                responseType: 'blob'
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'expenses.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error exporting expenses:', error);
        }
    };

    const getCategoryColor = (category) => {
        const colors = {
            Food: '#f59e0b',
            Transport: '#3b82f6',
            Entertainment: '#8b5cf6',
            Shopping: '#10b981',
            Bills: '#ef4444',
            Healthcare: '#06b6d4',
            Education: '#f97316',
            Travel: '#84cc16',
            Other: '#64748b'
        };
        return colors[category] || '#64748b';
    };

    if (loading) {
        return (
            <div className="container">
                <div className="loading">
                    <div className="spinner"></div>
                </div>
            </div>
        );
    }

    return (
        <div className="container">
            <div className="page-header">
                <h1 className="page-title">Expenses</h1>
                <p className="page-subtitle">Manage and track your expenses</p>
            </div>

            {/* Filters */}
            <div className="filters">
                <div className="flex justify-between items-center mb-4">
                    <div className="flex gap-2">
                        <button
                            className="btn btn-outline"
                            onClick={() => setShowFilters(!showFilters)}
                        >
                            <FiFilter />
                            Filters
                        </button>
                        <button
                            className="btn btn-outline"
                            onClick={handleExport}
                        >
                            <FiDownload />
                            Export CSV
                        </button>
                    </div>
                    <Link to="/expenses/add" className="btn btn-primary">
                        <FiPlus />
                        Add Expense
                    </Link>
                </div>

                {showFilters && (
                    <div className="filters-grid">
                        <div className="form-group">
                            <label className="form-label">Search</label>
                            <div className="input-group">
                                <FiSearch className="input-icon" />
                                <input
                                    type="text"
                                    name="search"
                                    value={filters.search}
                                    onChange={handleFilterChange}
                                    className="form-input"
                                    placeholder="Search expenses..."
                                />
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="form-label">Category</label>
                            <select
                                name="category"
                                value={filters.category}
                                onChange={handleFilterChange}
                                className="form-select"
                            >
                                <option value="">All Categories</option>
                                {categories.map(category => (
                                    <option key={category} value={category}>{category}</option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label className="form-label">Start Date</label>
                            <input
                                type="date"
                                name="startDate"
                                value={filters.startDate}
                                onChange={handleFilterChange}
                                className="form-input"
                            />
                        </div>
                        <div className="form-group">
                            <label className="form-label">End Date</label>
                            <input
                                type="date"
                                name="endDate"
                                value={filters.endDate}
                                onChange={handleFilterChange}
                                className="form-input"
                            />
                        </div>
                    </div>
                )}
            </div>

            {/* Expenses List */}
            {expenses.length > 0 ? (
                <div className="expense-list">
                    {expenses.map((expense) => (
                        <div key={expense._id} className="expense-item">
                            <div className="expense-info">
                                <div className="expense-title">{expense.title}</div>
                                <div className="expense-meta">
                                    <span
                                        className="expense-category"
                                        style={{ backgroundColor: getCategoryColor(expense.category) + '20', color: getCategoryColor(expense.category) }}
                                    >
                                        {expense.category}
                                    </span>
                                    <span>
                                        <FiCalendar className="inline mr-1" />
                                        {format(new Date(expense.date), 'MMM dd, yyyy')}
                                    </span>
                                    {expense.location && (
                                        <span>
                                            <FiMapPin className="inline mr-1" />
                                            {expense.location}
                                        </span>
                                    )}
                                    {expense.description && (
                                        <span className="text-gray-600">{expense.description}</span>
                                    )}
                                </div>
                            </div>
                            <div className="expense-actions">
                                <div className="expense-amount">${expense.amount.toFixed(2)}</div>
                                <div className="flex gap-2">
                                    <Link
                                        to={`/expenses/edit/${expense._id}`}
                                        className="btn-icon edit"
                                        title="Edit"
                                    >
                                        <FiEdit2 />
                                    </Link>
                                    <button
                                        onClick={() => handleDelete(expense._id)}
                                        className="btn-icon delete"
                                        title="Delete"
                                    >
                                        <FiTrash2 />
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="empty-state">
                    <div className="empty-state-icon">📝</div>
                    <div className="empty-state-title">No expenses found</div>
                    <div className="empty-state-text">
                        {Object.values(filters).some(f => f)
                            ? 'Try adjusting your filters or add a new expense.'
                            : 'Start tracking your expenses by adding your first transaction.'
                        }
                    </div>
                    <Link to="/expenses/add" className="btn btn-primary">
                        <FiPlus />
                        Add Expense
                    </Link>
                </div>
            )}

            {/* Pagination */}
            {totalPages > 1 && (
                <div className="pagination">
                    <button
                        className="pagination-btn"
                        onClick={() => setCurrentPage(currentPage - 1)}
                        disabled={currentPage === 1}
                    >
                        Previous
                    </button>

                    {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                        <button
                            key={page}
                            className={`pagination-btn ${currentPage === page ? 'active' : ''}`}
                            onClick={() => setCurrentPage(page)}
                        >
                            {page}
                        </button>
                    ))}

                    <button
                        className="pagination-btn"
                        onClick={() => setCurrentPage(currentPage + 1)}
                        disabled={currentPage === totalPages}
                    >
                        Next
                    </button>
                </div>
            )}
        </div>
    );
};

export default Expenses;
