import React, { useState, useEffect } from 'react';
import './BudgetSuggestions.css';

const BudgetSuggestions = ({ expenses, onApplySuggestion, onShowExpenseSplitter }) => {
    const [suggestions, setSuggestions] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (expenses && expenses.length > 0) {
            generateSuggestions();
        }
    }, [expenses]);

    const generateSuggestions = () => {
        setLoading(true);

        // Simulate API call delay
        setTimeout(() => {
            const categoryTotals = {};
            const categoryCounts = {};

            // Calculate totals and counts for each category
            expenses.forEach(expense => {
                if (expense.category) {
                    categoryTotals[expense.category] = (categoryTotals[expense.category] || 0) + expense.amount;
                    categoryCounts[expense.category] = (categoryCounts[expense.category] || 0) + 1;
                }
            });

            // Generate suggestions based on spending patterns
            const newSuggestions = Object.keys(categoryTotals).map(category => {
                const total = categoryTotals[category];
                const count = categoryCounts[category];
                const average = total / count;

                // Calculate suggested budget (average + 10% buffer)
                const suggestedBudget = Math.round(average * 1.1);

                // Determine spending trend
                let trend = 'stable';
                let trendColor = '#4CAF50';
                let trendIcon = '📊';

                if (total > average * 1.2) {
                    trend = 'increasing';
                    trendColor = '#FF9800';
                    trendIcon = '📈';
                } else if (total < average * 0.8) {
                    trend = 'decreasing';
                    trendColor = '#2196F3';
                    trendIcon = '📉';
                }

                return {
                    category,
                    currentTotal: total,
                    averagePerTransaction: average,
                    suggestedBudget,
                    trend,
                    trendColor,
                    trendIcon,
                    transactionCount: count,
                    savings: suggestedBudget - total
                };
            });

            // Sort by potential savings (highest first)
            newSuggestions.sort((a, b) => b.savings - a.savings);

            setSuggestions(newSuggestions);
            setLoading(false);
        }, 1000);
    };

    const handleApplySuggestion = (suggestion) => {
        // First call the original onApplySuggestion
        if (onApplySuggestion) {
            onApplySuggestion(suggestion);
        }

        // Then open the expense splitter with the suggestion data
        if (onShowExpenseSplitter) {
            onShowExpenseSplitter({
                description: `${suggestion.category} Budget`,
                amount: suggestion.suggestedBudget.toString(),
                category: suggestion.category
            });
        }
    };

    const getCategoryIcon = (category) => {
        const icons = {
            'Food': '🍕',
            'Transport': '🚗',
            'Shopping': '🛍️',
            'Bills': '📄',
            'Entertainment': '🎬',
            'Health': '💊',
            'Education': '📚',
            'Other': '📌'
        };
        return icons[category] || '📌';
    };

    if (loading) {
        return (
            <div className="budget-suggestions">
                <div className="suggestions-header">
                    <h3>💡 Smart Budget Suggestions</h3>
                    <div className="loading-spinner">Loading suggestions...</div>
                </div>
            </div>
        );
    }

    if (!suggestions || suggestions.length === 0) {
        return (
            <div className="budget-suggestions">
                <div className="suggestions-header">
                    <h3>💡 Smart Budget Suggestions</h3>
                    <p className="no-data">Add some expenses to get personalized budget suggestions!</p>
                </div>
            </div>
        );
    }

    return (
        <div className="budget-suggestions">
            <div className="suggestions-header">
                <h3>💡 Smart Budget Suggestions</h3>
                <p className="suggestions-subtitle">Based on your spending patterns</p>
            </div>

            <div className="suggestions-grid">
                {suggestions.map((suggestion, index) => (
                    <div key={suggestion.category} className="suggestion-card">
                        <div className="suggestion-header">
                            <div className="category-info">
                                <span className="category-icon">{getCategoryIcon(suggestion.category)}</span>
                                <span className="category-name">{suggestion.category}</span>
                            </div>
                            <div className="trend-indicator" style={{ color: suggestion.trendColor }}>
                                <span className="trend-icon">{suggestion.trendIcon}</span>
                                <span className="trend-text">{suggestion.trend}</span>
                            </div>
                        </div>

                        <div className="suggestion-stats">
                            <div className="stat-row">
                                <span className="stat-label">Current Total:</span>
                                <span className="stat-value">RPS {suggestion.currentTotal.toFixed(2)}</span>
                            </div>
                            <div className="stat-row">
                                <span className="stat-label">Avg per Transaction:</span>
                                <span className="stat-value">RPS {suggestion.averagePerTransaction.toFixed(2)}</span>
                            </div>
                            <div className="stat-row">
                                <span className="stat-label">Transactions:</span>
                                <span className="stat-value">{suggestion.transactionCount}</span>
                            </div>
                        </div>

                        <div className="suggestion-recommendation">
                            <div className="recommended-budget">
                                <span className="budget-label">Suggested Budget:</span>
                                <span className="budget-amount">RPS {suggestion.suggestedBudget}</span>
                            </div>

                            {suggestion.savings > 0 && (
                                <div className="potential-savings positive">
                                    <span>Potential Savings: RPS {suggestion.savings}</span>
                                </div>
                            )}

                            {suggestion.savings < 0 && (
                                <div className="potential-savings negative">
                                    <span>Over Budget: RPS {Math.abs(suggestion.savings)}</span>
                                </div>
                            )}
                        </div>

                        <button
                            className="apply-suggestion-btn"
                            onClick={() => handleApplySuggestion(suggestion)}
                        >
                            Apply Suggestion
                        </button>
                    </div>
                ))}
            </div>

            <div className="suggestions-footer">
                <p>💡 These suggestions are based on your spending history and include a 10% buffer for unexpected expenses.</p>
            </div>
        </div>
    );
};

export default BudgetSuggestions;
