import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { format, addDays, addWeeks, addMonths, addYears } from 'date-fns';
import {
    FiPlus,
    FiEdit2,
    FiTrash2,
    FiCalendar,
    FiDollarSign,
    FiRepeat,
    FiPlay,
    FiPause
} from 'react-icons/fi';

const Recurring = () => {
    const [recurringExpenses, setRecurringExpenses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [editingExpense, setEditingExpense] = useState(null);
    const [formData, setFormData] = useState({
        title: '',
        amount: '',
        category: '',
        description: '',
        frequency: 'monthly',
        startDate: new Date().toISOString().split('T')[0],
        endDate: '',
        paymentMethod: 'Cash',
        tags: ''
    });

    const categories = [
        'Food', 'Transport', 'Entertainment', 'Shopping',
        'Bills', 'Healthcare', 'Education', 'Travel', 'Other'
    ];

    const frequencies = [
        { value: 'daily', label: 'Daily' },
        { value: 'weekly', label: 'Weekly' },
        { value: 'monthly', label: 'Monthly' },
        { value: 'yearly', label: 'Yearly' }
    ];

    const paymentMethods = [
        'Cash', 'Credit Card', 'Debit Card', 'Bank Transfer', 'Digital Wallet'
    ];

    useEffect(() => {
        fetchRecurringExpenses();
    }, []);

    const fetchRecurringExpenses = async () => {
        try {
            setLoading(true);
            const response = await axios.get('/api/recurring');
            setRecurringExpenses(response.data);
        } catch (error) {
            console.error('Error fetching recurring expenses:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const expenseData = {
                ...formData,
                amount: parseFloat(formData.amount),
                tags: formData.tags ? formData.tags.split(',').map(tag => tag.trim()) : []
            };

            if (editingExpense) {
                await axios.put(`/api/recurring/${editingExpense._id}`, expenseData);
            } else {
                await axios.post('/api/recurring', expenseData);
            }

            setShowForm(false);
            setEditingExpense(null);
            resetForm();
            fetchRecurringExpenses();
        } catch (error) {
            console.error('Error saving recurring expense:', error);
        }
    };

    const handleEdit = (expense) => {
        setEditingExpense(expense);
        setFormData({
            title: expense.title,
            amount: expense.amount.toString(),
            category: expense.category,
            description: expense.description || '',
            frequency: expense.frequency,
            startDate: new Date(expense.startDate).toISOString().split('T')[0],
            endDate: expense.endDate ? new Date(expense.endDate).toISOString().split('T')[0] : '',
            paymentMethod: expense.paymentMethod,
            tags: expense.tags ? expense.tags.join(', ') : ''
        });
        setShowForm(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this recurring expense?')) {
            try {
                await axios.delete(`/api/recurring/${id}`);
                fetchRecurringExpenses();
            } catch (error) {
                console.error('Error deleting recurring expense:', error);
            }
        }
    };

    const handleAdvance = async (id) => {
        try {
            await axios.post(`/api/recurring/${id}/advance`);
            fetchRecurringExpenses();
        } catch (error) {
            console.error('Error advancing recurring expense:', error);
        }
    };

    const handleToggleActive = async (expense) => {
        try {
            await axios.put(`/api/recurring/${expense._id}`, {
                ...expense,
                isActive: !expense.isActive
            });
            fetchRecurringExpenses();
        } catch (error) {
            console.error('Error toggling recurring expense:', error);
        }
    };

    const resetForm = () => {
        setFormData({
            title: '',
            amount: '',
            category: '',
            description: '',
            frequency: 'monthly',
            startDate: new Date().toISOString().split('T')[0],
            endDate: '',
            paymentMethod: 'Cash',
            tags: ''
        });
    };

    const getNextDueDate = (expense) => {
        const nextDue = new Date(expense.nextDueDate);
        return format(nextDue, 'MMM dd, yyyy');
    };

    const isOverdue = (expense) => {
        return new Date(expense.nextDueDate) < new Date();
    };

    if (loading) {
        return (
            <div className="container">
                <div className="loading">
                    <div className="spinner"></div>
                </div>
            </div>
        );
    }

    return (
        <div className="container">
            <div className="page-header">
                <h1 className="page-title">Recurring Expenses</h1>
                <p className="page-subtitle">Manage your recurring expense patterns</p>
            </div>

            {/* Add/Edit Form */}
            {showForm && (
                <div className="card mb-6">
                    <div className="flex justify-between items-center mb-4">
                        <h3>{editingExpense ? 'Edit' : 'Add'} Recurring Expense</h3>
                        <button
                            onClick={() => {
                                setShowForm(false);
                                setEditingExpense(null);
                                resetForm();
                            }}
                            className="btn btn-outline btn-sm"
                        >
                            Cancel
                        </button>
                    </div>

                    <form onSubmit={handleSubmit}>
                        <div className="grid md:grid-cols-2 gap-4">
                            <div className="form-group">
                                <label className="form-label">Title *</label>
                                <input
                                    type="text"
                                    name="title"
                                    value={formData.title}
                                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                    className="form-input"
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label className="form-label">Amount *</label>
                                <input
                                    type="number"
                                    name="amount"
                                    value={formData.amount}
                                    onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                                    className="form-input"
                                    step="0.01"
                                    min="0"
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label className="form-label">Category *</label>
                                <select
                                    name="category"
                                    value={formData.category}
                                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                    className="form-select"
                                    required
                                >
                                    <option value="">Select category</option>
                                    {categories.map(cat => (
                                        <option key={cat} value={cat}>{cat}</option>
                                    ))}
                                </select>
                            </div>

                            <div className="form-group">
                                <label className="form-label">Frequency *</label>
                                <select
                                    name="frequency"
                                    value={formData.frequency}
                                    onChange={(e) => setFormData({ ...formData, frequency: e.target.value })}
                                    className="form-select"
                                    required
                                >
                                    {frequencies.map(freq => (
                                        <option key={freq.value} value={freq.value}>{freq.label}</option>
                                    ))}
                                </select>
                            </div>

                            <div className="form-group">
                                <label className="form-label">Start Date *</label>
                                <input
                                    type="date"
                                    name="startDate"
                                    value={formData.startDate}
                                    onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
                                    className="form-input"
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label className="form-label">End Date</label>
                                <input
                                    type="date"
                                    name="endDate"
                                    value={formData.endDate}
                                    onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
                                    className="form-input"
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label className="form-label">Description</label>
                            <textarea
                                name="description"
                                value={formData.description}
                                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                className="form-input"
                                rows="3"
                            />
                        </div>

                        <div className="flex gap-4">
                            <button type="submit" className="btn btn-primary">
                                {editingExpense ? 'Update' : 'Create'} Recurring Expense
                            </button>
                        </div>
                    </form>
                </div>
            )}

            {/* Recurring Expenses List */}
            <div className="card">
                <div className="flex justify-between items-center mb-4">
                    <h3>Recurring Expenses</h3>
                    <button
                        onClick={() => setShowForm(true)}
                        className="btn btn-primary"
                    >
                        <FiPlus />
                        Add Recurring Expense
                    </button>
                </div>

                {recurringExpenses.length > 0 ? (
                    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {recurringExpenses.map((expense) => (
                            <div key={expense._id} className="card">
                                <div className="flex justify-between items-start mb-2">
                                    <h4 className="font-semibold">{expense.title}</h4>
                                    <div className="flex gap-1">
                                        <button
                                            onClick={() => handleEdit(expense)}
                                            className="btn-icon edit"
                                            title="Edit"
                                        >
                                            <FiEdit2 />
                                        </button>
                                        <button
                                            onClick={() => handleDelete(expense._id)}
                                            className="btn-icon delete"
                                            title="Delete"
                                        >
                                            <FiTrash2 />
                                        </button>
                                    </div>
                                </div>

                                <div className="text-2xl font-bold mb-2">
                                    ${expense.amount.toFixed(2)}
                                </div>

                                <div className="space-y-1 text-sm text-gray-600 mb-3">
                                    <div className="flex items-center">
                                        <FiRepeat className="mr-1" />
                                        {expense.frequency.charAt(0).toUpperCase() + expense.frequency.slice(1)}
                                    </div>
                                    <div className="flex items-center">
                                        <FiCalendar className="mr-1" />
                                        Next: {getNextDueDate(expense)}
                                    </div>
                                    {isOverdue(expense) && (
                                        <div className="text-red-600 font-medium">
                                            Overdue!
                                        </div>
                                    )}
                                </div>

                                <div className="flex gap-2">
                                    <button
                                        onClick={() => handleAdvance(expense._id)}
                                        className="btn btn-outline btn-sm"
                                        title="Mark as paid and advance to next due date"
                                    >
                                        <FiPlay />
                                        Mark Paid
                                    </button>
                                    <button
                                        onClick={() => handleToggleActive(expense)}
                                        className={`btn btn-sm ${expense.isActive ? 'btn-warning' : 'btn-success'}`}
                                        title={expense.isActive ? 'Pause' : 'Activate'}
                                    >
                                        {expense.isActive ? <FiPause /> : <FiPlay />}
                                        {expense.isActive ? 'Pause' : 'Activate'}
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                ) : (
                    <div className="empty-state">
                        <div className="empty-state-icon">ðŸ”„</div>
                        <div className="empty-state-title">No recurring expenses</div>
                        <div className="empty-state-text">
                            Set up recurring expenses to automatically track regular payments.
                        </div>
                        <button
                            onClick={() => setShowForm(true)}
                            className="btn btn-primary"
                        >
                            <FiPlus />
                            Add Your First Recurring Expense
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Recurring;
