import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiHeart, FiTrendingUp, FiTrendingDown, FiSmile, FiActivity, FiTarget, FiBookOpen, FiZap, FiPlay } from 'react-icons/fi';
import WellnessGame from '../components/WellnessGame';

const Wellness = () => {
    const [expenses, setExpenses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedPeriod, setSelectedPeriod] = useState('month');
    const [showWellnessGame, setShowWellnessGame] = useState(false);

    useEffect(() => {
        fetchExpenses();
    }, [selectedPeriod]);

    const fetchExpenses = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`/api/expenses?period=${selectedPeriod}`);
            setExpenses(response.data);
        } catch (error) {
            setError('Failed to fetch expenses');
            console.error('Error fetching expenses:', error);
        } finally {
            setLoading(false);
        }
    };

    // Calculate mood-spending insights
    const calculateMoodInsights = () => {
        const moodData = {};
        let totalSpent = 0;
        let emotionalSpendingCount = 0;
        let emotionalSpendingTotal = 0;

        expenses.forEach(expense => {
            const mood = expense.mood || 'neutral';
            if (!moodData[mood]) {
                moodData[mood] = { count: 0, total: 0, avgAmount: 0 };
            }
            moodData[mood].count++;
            moodData[mood].total += expense.amount;
            totalSpent += expense.amount;

            if (expense.emotionalSpending) {
                emotionalSpendingCount++;
                emotionalSpendingTotal += expense.amount;
            }
        });

        // Calculate averages
        Object.keys(moodData).forEach(mood => {
            moodData[mood].avgAmount = moodData[mood].total / moodData[mood].count;
        });

        return {
            moodData,
            totalSpent,
            emotionalSpendingCount,
            emotionalSpendingTotal,
            emotionalSpendingPercentage: totalSpent > 0 ? (emotionalSpendingTotal / totalSpent) * 100 : 0
        };
    };

    // Get wellness tips based on spending patterns
    const getWellnessTips = () => {
        const insights = calculateMoodInsights();
        const tips = [];

        // Tip based on emotional spending
        if (insights.emotionalSpendingPercentage > 30) {
            tips.push({
                icon: '💭',
                title: 'Emotional Spending Alert',
                content: 'You\'re spending a significant amount when emotional. Try waiting 24 hours before making non-essential purchases.',
                type: 'warning'
            });
        }

        // Tip based on mood patterns
        const moodData = insights.moodData;
        if (moodData.sad && moodData.sad.avgAmount > 50) {
            tips.push({
                icon: '😢',
                title: 'Sadness & Spending',
                content: 'You tend to spend more when feeling sad. Consider alternative activities like calling a friend or going for a walk.',
                type: 'insight'
            });
        }

        if (moodData.stressed && moodData.stressed.avgAmount > 40) {
            tips.push({
                icon: '😰',
                title: 'Stress Spending Pattern',
                content: 'Stress seems to trigger higher spending. Try deep breathing exercises before making purchases.',
                type: 'insight'
            });
        }

        // General wellness tips
        tips.push({
            icon: '🎯',
            title: 'Mindful Spending',
            content: 'Before each purchase, ask yourself: "Do I need this, or do I want this?" Take a moment to reflect.',
            type: 'tip'
        });

        tips.push({
            icon: '📱',
            title: 'Digital Detox',
            content: 'Unsubscribe from shopping emails and remove shopping apps from your phone to reduce impulse buying.',
            type: 'tip'
        });

        return tips;
    };

    const insights = calculateMoodInsights();
    const wellnessTips = getWellnessTips();

    const moodEmojis = {
        happy: '😊',
        sad: '😢',
        stressed: '😰',
        excited: '🤩',
        neutral: '😐',
        anxious: '😨',
        content: '😌',
        frustrated: '😤'
    };

    if (loading) {
        return (
            <div className="container">
                <div className="page-header">
                    <h1 className="page-title">Financial Wellness</h1>
                    <p className="page-subtitle">Understanding your spending patterns and mental health</p>
                </div>
                <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
                    <p className="mt-4 text-gray-600">Loading your wellness insights...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="container">
            <div className="page-header">
                <h1 className="page-title flex items-center">
                    <FiHeart className="mr-3 text-red-500" />
                    Financial Wellness
                </h1>
                <p className="page-subtitle">Understanding your spending patterns and mental health</p>
            </div>

            {error && (
                <div className="alert alert-error mb-6">
                    {error}
                </div>
            )}

            {/* Period Selector */}
            <div className="mb-6">
                <div className="flex gap-2">
                    {['week', 'month', 'quarter', 'year'].map(period => (
                        <button
                            key={period}
                            onClick={() => setSelectedPeriod(period)}
                            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${selectedPeriod === period
                                    ? 'bg-blue-600 text-white'
                                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                                }`}
                        >
                            {period.charAt(0).toUpperCase() + period.slice(1)}
                        </button>
                    ))}
                </div>
            </div>

            {/* Key Metrics */}
            <div className="grid md:grid-cols-3 gap-6 mb-8">
                <div className="bg-white p-6 rounded-lg shadow-sm border">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-sm text-gray-600">Total Spent</p>
                            <p className="text-2xl font-bold text-gray-900">${insights.totalSpent.toFixed(2)}</p>
                        </div>
                        <div className="text-3xl">💰</div>
                    </div>
                </div>

                <div className="bg-white p-6 rounded-lg shadow-sm border">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-sm text-gray-600">Emotional Purchases</p>
                            <p className="text-2xl font-bold text-gray-900">{insights.emotionalSpendingCount}</p>
                        </div>
                        <div className="text-3xl">💭</div>
                    </div>
                </div>

                <div className="bg-white p-6 rounded-lg shadow-sm border">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-sm text-gray-600">Emotional Spending %</p>
                            <p className="text-2xl font-bold text-gray-900">{insights.emotionalSpendingPercentage.toFixed(1)}%</p>
                        </div>
                        <div className="text-3xl">📊</div>
                    </div>
                </div>
            </div>

            {/* Mood-Spending Analysis */}
            <div className="grid lg:grid-cols-2 gap-8 mb-8">
                <div className="bg-white p-6 rounded-lg shadow-sm border">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                        <FiActivity className="mr-2" />
                        Mood-Spending Patterns
                    </h3>
                    <div className="space-y-4">
                        {Object.entries(insights.moodData).map(([mood, data]) => (
                            <div key={mood} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                                <div className="flex items-center">
                                    <span className="text-2xl mr-3">{moodEmojis[mood]}</span>
                                    <div>
                                        <p className="font-medium text-gray-900 capitalize">{mood}</p>
                                        <p className="text-sm text-gray-600">{data.count} purchases</p>
                                    </div>
                                </div>
                                <div className="text-right">
                                    <p className="font-semibold text-gray-900">${data.total.toFixed(2)}</p>
                                    <p className="text-sm text-gray-600">Avg: ${data.avgAmount.toFixed(2)}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Wellness Tips */}
                <div className="bg-white p-6 rounded-lg shadow-sm border">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                        <FiBookOpen className="mr-2" />
                        Wellness Tips
                    </h3>
                    <div className="space-y-4">
                        {wellnessTips.map((tip, index) => (
                            <div key={index} className={`p-4 rounded-lg border-l-4 ${tip.type === 'warning' ? 'border-red-400 bg-red-50' :
                                    tip.type === 'insight' ? 'border-blue-400 bg-blue-50' :
                                        'border-green-400 bg-green-50'
                                }`}>
                                <div className="flex items-start">
                                    <span className="text-2xl mr-3">{tip.icon}</span>
                                    <div>
                                        <h4 className="font-medium text-gray-900 mb-1">{tip.title}</h4>
                                        <p className="text-sm text-gray-700">{tip.content}</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Wellness Game Section */}
            <div className="bg-gradient-to-r from-purple-500 to-pink-600 p-6 rounded-lg text-white mb-8">
                <div className="flex items-center justify-between">
                    <div>
                        <h3 className="text-lg font-semibold mb-2 flex items-center">
                            <FiPlay className="mr-2" />
                            Interactive Wellness Games
                        </h3>
                        <p className="text-sm opacity-90">
                            Practice mindful spending with interactive exercises and build healthier financial habits.
                        </p>
                    </div>
                    <button
                        onClick={() => setShowWellnessGame(true)}
                        className="bg-white text-purple-600 px-6 py-2 rounded-lg font-medium hover:bg-gray-100 transition-colors"
                    >
                        Play Games
                    </button>
                </div>
            </div>

            {/* Quick Wellness Actions */}
            <div className="bg-gradient-to-r from-blue-500 to-purple-600 p-6 rounded-lg text-white">
                <h3 className="text-lg font-semibold mb-4 flex items-center">
                    <FiZap className="mr-2" />
                    Quick Wellness Actions
                </h3>
                <div className="grid md:grid-cols-3 gap-4">
                    <div className="text-center">
                        <div className="text-3xl mb-2">🧘‍♀️</div>
                        <p className="text-sm">Take 3 deep breaths before making a purchase</p>
                    </div>
                    <div className="text-center">
                        <div className="text-3xl mb-2">📝</div>
                        <p className="text-sm">Write down your feelings before spending</p>
                    </div>
                    <div className="text-center">
                        <div className="text-3xl mb-2">⏰</div>
                        <p className="text-sm">Wait 24 hours for non-essential purchases</p>
                    </div>
                </div>
            </div>

            {/* Wellness Game Modal */}
            <WellnessGame
                isOpen={showWellnessGame}
                onClose={() => setShowWellnessGame(false)}
            />
        </div>
    );
};

export default Wellness;
