import React, { useState, useEffect } from 'react';
import './CurrencyConverter.css';

const CurrencyConverter = ({ isOpen, onClose }) => {
    const [amount, setAmount] = useState('');
    const [fromCurrency, setFromCurrency] = useState('INR');
    const [toCurrency, setToCurrency] = useState('USD');
    const [convertedAmount, setConvertedAmount] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const currencies = [
        { code: 'INR', name: 'Indian Rupee', symbol: 'RPS' },
        { code: 'USD', name: 'US Dollar', symbol: '$' },
        { code: 'EUR', name: 'Euro', symbol: '€' },
        { code: 'GBP', name: 'British Pound', symbol: '£' },
        { code: 'JPY', name: 'Japanese Yen', symbol: '¥' },
        { code: 'CAD', name: 'Canadian Dollar', symbol: 'C$' },
        { code: 'AUD', name: 'Australian Dollar', symbol: 'A$' },
        { code: 'CHF', name: 'Swiss Franc', symbol: 'CHF' }
    ];

    const convertCurrency = async () => {
        if (!amount || amount <= 0) {
            setError('Please enter a valid amount');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const response = await fetch(`https://api.exchangerate.host/convert?from=${fromCurrency}&to=${toCurrency}&amount=${amount}`);
            const data = await response.json();

            if (data.success) {
                setConvertedAmount(data.result.toFixed(2));
            } else {
                setError('Conversion failed. Please try again.');
            }
        } catch (err) {
            setError('Network error. Please check your connection.');
        } finally {
            setLoading(false);
        }
    };

    const handleSwap = () => {
        setFromCurrency(toCurrency);
        setToCurrency(fromCurrency);
        setConvertedAmount('');
    };

    const getCurrencySymbol = (code) => {
        const currency = currencies.find(c => c.code === code);
        return currency ? currency.symbol : code;
    };

    if (!isOpen) return null;

    return (
        <div className="currency-converter-overlay" onClick={onClose}>
            <div className="currency-converter-modal" onClick={(e) => e.stopPropagation()}>
                <div className="currency-converter-header">
                    <h3>💱 Currency Converter</h3>
                    <button className="close-btn" onClick={onClose}>×</button>
                </div>

                <div className="currency-converter-content">
                    <div className="conversion-inputs">
                        <div className="input-group">
                            <label>From</label>
                            <div className="amount-input">
                                <span className="currency-symbol">{getCurrencySymbol(fromCurrency)}</span>
                                <input
                                    type="number"
                                    value={amount}
                                    onChange={(e) => setAmount(e.target.value)}
                                    placeholder="Enter amount"
                                    min="0"
                                    step="0.01"
                                />
                            </div>
                            <select
                                value={fromCurrency}
                                onChange={(e) => setFromCurrency(e.target.value)}
                            >
                                {currencies.map(currency => (
                                    <option key={currency.code} value={currency.code}>
                                        {currency.code} - {currency.name}
                                    </option>
                                ))}
                            </select>
                        </div>

                        <button className="swap-btn" onClick={handleSwap}>
                            ⇄ Swap
                        </button>

                        <div className="input-group">
                            <label>To</label>
                            <div className="amount-input">
                                <span className="currency-symbol">{getCurrencySymbol(toCurrency)}</span>
                                <input
                                    type="text"
                                    value={convertedAmount}
                                    readOnly
                                    placeholder="Converted amount"
                                    className="result-input"
                                />
                            </div>
                            <select
                                value={toCurrency}
                                onChange={(e) => setToCurrency(e.target.value)}
                            >
                                {currencies.map(currency => (
                                    <option key={currency.code} value={currency.code}>
                                        {currency.code} - {currency.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>

                    {error && <div className="error-message">{error}</div>}

                    <button
                        className="convert-btn"
                        onClick={convertCurrency}
                        disabled={loading || !amount}
                    >
                        {loading ? 'Converting...' : 'Convert'}
                    </button>

                    <div className="converter-info">
                        <p>💡 Tip: Use this for international transactions, online shopping, or travel planning!</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CurrencyConverter;
