import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { format } from 'date-fns';
import {
    FiDollarSign,
    FiTrendingUp,
    FiTrendingDown,
    FiPieChart,
    FiPlus,
    FiCalendar,
    FiMapPin
} from 'react-icons/fi';
import { Pie, Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
} from 'chart.js';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement
);

const Dashboard = () => {
    const [summary, setSummary] = useState(null);
    const [recentExpenses, setRecentExpenses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
    const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());

    useEffect(() => {
        fetchDashboardData();
    }, [selectedMonth, selectedYear]);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            const [summaryRes, expensesRes] = await Promise.all([
                axios.get(`/api/expenses/summary?month=${selectedMonth}&year=${selectedYear}`),
                axios.get('/api/expenses?limit=5&sortBy=date&sortOrder=desc')
            ]);

            setSummary(summaryRes.data);
            setRecentExpenses(expensesRes.data.expenses);
        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    const getCategoryColor = (category) => {
        const colors = {
            Food: '#f59e0b',
            Transport: '#3b82f6',
            Entertainment: '#8b5cf6',
            Shopping: '#10b981',
            Bills: '#ef4444',
            Healthcare: '#06b6d4',
            Education: '#f97316',
            Travel: '#84cc16',
            Other: '#64748b'
        };
        return colors[category] || '#64748b';
    };

    const pieChartData = {
        labels: Object.keys(summary?.categoryTotals || {}),
        datasets: [
            {
                data: Object.values(summary?.categoryTotals || {}),
                backgroundColor: Object.keys(summary?.categoryTotals || {}).map(getCategoryColor),
                borderWidth: 2,
                borderColor: '#ffffff'
            }
        ]
    };

    const lineChartData = {
        labels: Object.keys(summary?.dailyTotals || {}).map(date =>
            format(new Date(date), 'MMM dd')
        ),
        datasets: [
            {
                label: 'Daily Expenses',
                data: Object.values(summary?.dailyTotals || {}),
                borderColor: '#2563eb',
                backgroundColor: 'rgba(37, 99, 235, 0.1)',
                tension: 0.4,
                fill: true
            }
        ]
    };

    const chartOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom'
            }
        }
    };

    if (loading) {
        return (
            <div className="container">
                <div className="loading">
                    <div className="spinner"></div>
                </div>
            </div>
        );
    }

    return (
        <div className="container">
            <div className="page-header">
                <h1 className="page-title">Dashboard</h1>
                <p className="page-subtitle">
                    Overview of your expenses for {format(new Date(selectedYear, selectedMonth - 1), 'MMMM yyyy')}
                </p>
            </div>

            {/* Month/Year Selector */}
            <div className="filters mb-4">
                <div className="filters-grid">
                    <div className="form-group">
                        <label className="form-label">Month</label>
                        <select
                            className="form-select"
                            value={selectedMonth}
                            onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
                        >
                            {Array.from({ length: 12 }, (_, i) => i + 1).map(month => (
                                <option key={month} value={month}>
                                    {format(new Date(2024, month - 1), 'MMMM')}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label className="form-label">Year</label>
                        <select
                            className="form-select"
                            value={selectedYear}
                            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                        >
                            {Array.from({ length: 5 }, (_, i) => new Date().getFullYear() - 2 + i).map(year => (
                                <option key={year} value={year}>{year}</option>
                            ))}
                        </select>
                    </div>
                </div>
            </div>

            {/* Stats Cards */}
            <div className="stats-grid">
                <div className="stat-card">
                    <div className="stat-value">${summary?.totalAmount?.toFixed(2) || '0.00'}</div>
                    <div className="stat-label">Total Expenses</div>
                </div>
                <div className="stat-card success">
                    <div className="stat-value">{summary?.expenseCount || 0}</div>
                    <div className="stat-label">Total Transactions</div>
                </div>
                <div className="stat-card warning">
                    <div className="stat-value">
                        ${summary?.totalAmount ? (summary.totalAmount / summary.expenseCount).toFixed(2) : '0.00'}
                    </div>
                    <div className="stat-label">Average per Transaction</div>
                </div>
                <div className="stat-card">
                    <div className="stat-value">
                        ${summary?.totalAmount ? (summary.totalAmount / new Date(selectedYear, selectedMonth, 0).getDate()).toFixed(2) : '0.00'}
                    </div>
                    <div className="stat-label">Daily Average</div>
                </div>
            </div>

            {/* Charts Section */}
            <div className="grid md:grid-cols-2 gap-6 mb-6">
                <div className="chart-container">
                    <h3 className="chart-title">Expenses by Category</h3>
                    {Object.keys(summary?.categoryTotals || {}).length > 0 ? (
                        <Pie data={pieChartData} options={chartOptions} />
                    ) : (
                        <div className="empty-state">
                            <p>No expenses data available</p>
                        </div>
                    )}
                </div>
                <div className="chart-container">
                    <h3 className="chart-title">Daily Expenses Trend</h3>
                    {Object.keys(summary?.dailyTotals || {}).length > 0 ? (
                        <Line data={lineChartData} options={chartOptions} />
                    ) : (
                        <div className="empty-state">
                            <p>No daily data available</p>
                        </div>
                    )}
                </div>
            </div>

            {/* Recent Expenses */}
            <div className="card">
                <div className="flex justify-between items-center mb-4">
                    <h3>Recent Expenses</h3>
                    <Link to="/expenses/add" className="btn btn-primary btn-sm">
                        <FiPlus />
                        Add Expense
                    </Link>
                </div>

                {recentExpenses.length > 0 ? (
                    <div className="expense-list">
                        {recentExpenses.map((expense) => (
                            <div key={expense._id} className="expense-item">
                                <div className="expense-info">
                                    <div className="expense-title">{expense.title}</div>
                                    <div className="expense-meta">
                                        <span className={`expense-category category-${expense.category.toLowerCase()}`}>
                                            {expense.category}
                                        </span>
                                        <span>
                                            <FiCalendar className="inline mr-1" />
                                            {format(new Date(expense.date), 'MMM dd, yyyy')}
                                        </span>
                                        {expense.location && (
                                            <span>
                                                <FiMapPin className="inline mr-1" />
                                                {expense.location}
                                            </span>
                                        )}
                                    </div>
                                </div>
                                <div className="expense-amount">${expense.amount.toFixed(2)}</div>
                            </div>
                        ))}
                    </div>
                ) : (
                    <div className="empty-state">
                        <div className="empty-state-icon">ðŸ“Š</div>
                        <div className="empty-state-title">No expenses yet</div>
                        <div className="empty-state-text">
                            Start tracking your expenses by adding your first transaction.
                        </div>
                        <Link to="/expenses/add" className="btn btn-primary">
                            <FiPlus />
                            Add Your First Expense
                        </Link>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
